(25325, '98', 'A balance.', 37),
(25326, '99', 'No balance any more.', 37),
(25327, '1', 'Here we are.', 38),
(25328, '10', 'Too bad you got a hangnail for a cock.', 38),
(25329, '100', 'do you remember that?', 38),
(25330, '101', 'I\'m sorry.', 38),
(25331, '102', '- I\'m sorry they did that to you. - Why?', 38),
