(25339, '11', 'First time I saw you with your breeches down, I thought you were a eunuch.', 38),
(25340, '110', 'I never meet Missandei from the island of Naath.', 38),
(25341, '111', 'I am sorry I...', 38),
(25342, '112', 'For today,', 38),
(25343, '113', 'I am sorry.', 38),
(25344, '114', 'Grey Worm.', 38),
(25345, '115', 'I\'m glad you saw me.', 38),
(25346, '116', 'So am I.', 38),
(25347, '117', 'Kraken.', 38),
(25348, '118', 'Mmm. Strong, as long as they\'re in the sea.', 38),
(25349, '119', 'When you take them out of the water, no bones.', 38),
(25350, '12', 'You\'ve seen it, girls.', 38),
(25351, '120', 'They collapse under their proud weight and slump into a heap of nothing.', 38),
