(25359, '128', 'You do look very much like a lord.', 38),
(25360, '129', 'Formidable, proud.', 38),
(25361, '13', 'Like a baby snail peeking out of its shell.', 38),
(25362, '130', 'I\'m Reek.', 38),
(25363, '131', 'Until when?', 38),
(25364, '132', 'Always.', 38),
(25365, '133', '- Forever. - That\'s right.', 38),
(25366, '134', 'Until you\'re rotting in the ground.', 38),
(25367, '135', 'Remember what you are and what you\'re not.', 38),
(25368, '136', 'Bring me Moat Cailin.', 38),
(25369, '137', 'No closer.', 38),
(25370, '138', 'Who are you?', 38),
(25371, '139', 'I\'m Kenning,', 38),
(25372, '14', 'Your baby was crying this morning.', 38),
(25373, '140', 'commander of this garrison.', 38),
