(25480, '237', 'He can stay.', 38),
(25481, '238', 'Speak up, girl, you\'re not a damn kitchen mouse.', 38),
(25482, '239', 'Tell us what you saw.', 38),
(25483, '24', '- Are you listening to me? - Shut your mouth.', 38),
(25484, '240', 'I\'m sorry, Lord Baelish.', 38),
(25485, '241', 'I have to tell the truth.', 38),
(25486, '242', 'I\'ll tell you everything.', 38),
(25487, '243', 'Please, Alayne, leave nothing out.', 38),
(25488, '244', 'My name is not Alayne.', 38),
(25489, '245', 'It\'s Sansa Stark,', 38),
(25490, '246', 'eldest daughter of Lord Eddard Stark.', 38),
(25491, '247', 'Lord Royce, we met when you came to Winterfell.', 38),
(25492, '248', 'You were escorting your son Ser Waymar to the Wall.', 38),
(25493, '249', 'Sansa Stark?', 38),
